mapper.insert:

  SELECT LAST_INSERT_ID()

insert into t_cart_item (foodId, number, cartId
  )
values (#{foodId,jdbcType=INTEGER}, #{number,jdbcType=INTEGER}, #{cartId,jdbcType=INTEGER}
  )

mapper.update:
update t_cart_item
set foodId = #{foodId,jdbcType=INTEGER},
  number = #{number,jdbcType=INTEGER},
  cartId = #{cartId,jdbcType=INTEGER}
where itemId = #{itemId,jdbcType=INTEGER}

mapper.select:
select itemId, foodId, number, cartId
from t_cart_item
where itemId = #{itemId,jdbcType=INTEGER}

mapper.update:
update t_cart
set businessId = #{businessId,jdbcType=INTEGER},
  userId = #{userId,jdbcType=INTEGER}
where cartId = #{cartId,jdbcType=INTEGER}

mapper.select:
select cartId, businessId, userId
from t_cart
where cartId = #{cartId,jdbcType=INTEGER}
