mapper.insert:

  SELECT LAST_INSERT_ID()

insert into t_order_detail (orderId, foodId, number
  )
values (#{orderId,jdbcType=INTEGER}, #{foodId,jdbcType=INTEGER}, #{number,jdbcType=INTEGER}
  )

mapper.update:
update t_order_detail
set orderId = #{orderId,jdbcType=INTEGER},
  foodId = #{foodId,jdbcType=INTEGER},
  number = #{number,jdbcType=INTEGER}
where detailId = #{detailId,jdbcType=INTEGER}

mapper.select:
select detailId, orderId, foodId, number
from t_order_detail
where detailId = #{detailId,jdbcType=INTEGER}
